mapper.update:
{
    CALL
        DECLARE
        BEGIN

        UPDATE course_info SET csCode = SUBSTR(#{csCode}, 0, 16) || 'C'
        WHERE csCode = #{csCode};

        UPDATE course SET csCode = SUBSTR(#{csCode}, 0, 16) || 'C'
        WHERE csCode = #{csCode};

    END
}

{
    CALL
        DECLARE
        BEGIN

        UPDATE course SET csCode = SUBSTR(#{csCode}, 0, 16) || 'N'
        WHERE csCode = #{csCode};

        UPDATE course_info SET csCode = SUBSTR(#{csCode}, 0, 16) || 'N'
        WHERE csCode = #{csCode};

    END
}
